import React, { useEffect, useState } from 'react';
import styles from './Menu.module.css';
import { CSSTransition } from 'react-transition-group';

export default function Menu({ page_list, set_page_index, page_index }) {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const isMobile = windowWidth < 600;

  return (
    <div className={styles.menu}>
      <div
        className={styles.logoName}
        onClick={() => set_page_index(Object.keys(page_list)[0])}
      >
        <div className={styles.info}>INFO</div>
        <div>TARKOV</div>
      </div>

      <div className={styles.navbar}>
        {Object.keys(page_list).map((e) => (
          page_list[e] !== '' && !isMobile ? (
            <CSSTransition
              key={e}
              in={!isMobile}
              timeout={500}
              classNames={styles.navbar_transition}
            >
              <button
                id={page_index === e ? styles.isOn : ''}
                className={styles.nav_button}
                onClick={() => set_page_index(e)}
              >
                {page_list[e]}
              </button>
            </CSSTransition>
          ) : null
        ))}
      </div>

      <div className={styles.burger}>
        {isMobile && <div className={styles.burger_button}>Burger</div>}
      </div>
    </div>
  );
}
