import React, { useState, useEffect } from 'react';
import styles from './target.module.css';

export default function Target({timeOfLife, x, y, color, size, speed}) {

    const [vis, setVis] = useState(true);

    const deadline = Date.now();

    time *= 0.99;

    const getTime = () => {

        const time = Date.now() - deadline;

        if(Math.floor(time / 1000) >= timeOfLife){
            setVis(false);
        }

    };

    useEffect(() => {
        const interval = setInterval(() => getTime(deadline), 1000);

        return () => clearInterval(interval);
      }, []);




    const style = {backgroundColor : color, left : x, top: y, animationDuration: "1s"}


    return (
        <>
        {vis && <div className={styles.target} style={style}></div>}
        </>
    );
}
