import React, { useState, useEffect } from 'react';
import Menu from './menu/Menu.jsx';
import styles from './App.module.css';

import Home from './pages/home/index.jsx';
import Ammo from './pages/ammo/index.jsx';
import Quests from './pages/quests/index.jsx';
import News from './pages/news/index.jsx';
import Posts from './pages/posts/index.jsx';
import Shooting from './pages/shooting/index.jsx';
import Assembly from './pages/assembly/index.jsx';

export default function App() {

  const [isMobile, setMobile] = useState(window.innerWidth < 800);

  useEffect(() => {
      const handleResize = () => {
        setMobile((window.innerWidth < 800));
      };

      window.addEventListener('resize', handleResize);

      return () => {
          window.removeEventListener('resize', handleResize);
      };
  }, []);

  let page_list = {
    "Home" : "", 
    "Ammo" : "Патроны",
    "Quests" : "Квесты",
    "News" : "Новости",
    "Posts" : "Посты",
    "Shooting" : "Тир",
    "Assembly" : "Сборки",
  }

  let [page_index, set_page_index] = useState(Object.keys(page_list)[0]);

  return (
    <div className={styles.app}>


      <Menu page_list={page_list} set_page_index={set_page_index} page_index={page_index}/>

      <div className={styles.main_div}>

        <div className={styles.background}>


          <div className={styles.central_block} style={{ width: isMobile ? "100%" : "40%"}}>

              {page_index === "Home" ? <Home page_list={page_list} set_page_index={set_page_index}/>: ""}
              {page_index === "Ammo" ? <Ammo/>: ""}
              {page_index === "Quests" ? <Quests/>: ""}
              {page_index === "News" ? <News/>: ""}
              {page_index === "Posts" ? <Posts/>: ""}
              {page_index === "Shooting" ? <Shooting/>: ""}
              {page_index === "Assembly" ? <Assembly/>: ""}

          </div>

        </div>

      </div>

    </div>
  );
}
