import React from "react";
import InputSearcher from "../../components/InputSearcher";
import CheckButton from "../../components/CheckButton";

import styles from './finderBlock.module.css'

export default function FinderBlock({all_caliber, filter_ammo, set_filter_ammo}){

    function updateFilter(valueInput){
        const foundAmmo = all_caliber.filter(ammo => ammo.toLowerCase().includes(valueInput.toLowerCase()));
        if(foundAmmo.length === 1 && foundAmmo.length !== filter_ammo){
            set_filter_ammo(foundAmmo[0]);
        }
      //  console.log(filter_ammo, valueInput)
    }

    return(

        <div className={styles.finder_div}>
            <InputSearcher defaultValue={"Поиск по калибру..."} callback={updateFilter}/>
            <div className={styles.div_button}>
                {all_caliber.map((e) => 
                    <CheckButton 
                    key={e} 
                    callback={updateFilter}
                    isOn={filter_ammo === e ? true : false}>
                        {e}
                    </CheckButton>)}
            </div>
        </div>

    );

}
