import React, { useState } from 'react'
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import styles from './index.module.css';
import './index.css'
import MainTitle from '../../components/MainTitle';
import MyButtonValue from '../../components/MyButtonValue';
import ChangerWindow from './changerWindow.jsx';
import WindowGame from './windowGame.jsx';
import ResultWindow from './resultWindow';


export default function Shooting() {

  const [idScreen, setIdScreen] = useState(0);
  const [level, setLevel] = useState(0)
  const [duration, setDuration] = useState(0)

  const levelDict = {"Easy" : 0, "Normal" : 1, "Hard" : 2};
  const durationDict = {"10 sec" : 10, "20 sec" : 20, "30 sec" : 30};

  const defaultSettings = { 
    0 : {name : "easy", k : 1.8, size : 120, speed : 2, speedSpawn : 1500 }, 
    1 : {name : "normal", k : 1.5, size : 60, speed : 1.5, speedSpawn : 1200 }, 
    2 : {name : "hard", k : 1.5, size : 40, speed : 1, speedSpawn : 1000 }, }

  function updateData(value, func){
    if(value < 0){
      setIdScreen(idScreen-1);
      return;
    }

    func(value);
    setIdScreen(idScreen+1);
  }

  const [data, setData] = useState({});

  return (
    <div className='full_size main_block'>

        <MainTitle title="Тир" />

        <TransitionGroup className={styles.screen_game}>

          {idScreen == 0 &&
          <CSSTransition
          in={idScreen == 0}
         timeout={500}          
timein={300}
          classNames="screen-game"
          unmountOnExit>
            <div className={styles.start_window}>
              <h1>Aim Trainer</h1>
              <button className={styles.btn} onClick={((e) => setIdScreen(1))}>Start</button>
            </div>
          </CSSTransition>}

          {idScreen == 1 && 
          <CSSTransition
          in={idScreen == 0}
         timeout={500}          
timein={300}
          classNames="screen-game"
          unmountOnExit>
            <ChangerWindow 
              title={"Choose difficulty level"}
              options={levelDict}
              callback={updateData}
              setValue={setLevel}
            />
          </CSSTransition>}

          {idScreen == 2 && 
           <CSSTransition
           in={idScreen == 0}
            timeout={500}           
            timein={300}
           classNames="screen-game"
           unmountOnExit>
          <ChangerWindow 
            title={"Choose duration"}
            options={durationDict}
            callback={updateData}
            setValue={setDuration}
          />
          </CSSTransition>}

          {idScreen == 3 && 
           <CSSTransition
            in={idScreen == 0}
            timeout={500}           
            timein={300}
            classNames="screen-game"
            unmountOnExit>
            <WindowGame 
            settings={defaultSettings[level]} 
            duration={duration} 
            nextWindow={(() => setIdScreen(4))} 
            setData={setData}/>
          </CSSTransition>}

          {idScreen == 4 && 
          <CSSTransition
          in={idScreen == 0}
          timeout={500}          
          timein={300}
          classNames="screen-game"
          unmountOnExit>
            <ResultWindow 
            data={data} 
            nextWindow={(() => setIdScreen(0))}/>
          </CSSTransition>}

       </TransitionGroup>

    </div>
  );
}
