import React, { useEffect, useState } from 'react';
import styles from './Menu.module.css';
import stylesCSS from './Menu.css'
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Burger from './Burger';
import ButtonPage from './ButtonPage';



export default function Menu({ page_list, set_page_index, page_index }) {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const isMobile = windowWidth < 850;

  return (
    <div className={styles.menu}>
      <div
        className={styles.logoName}
        onClick={() => set_page_index(Object.keys(page_list)[0])}
      >
        <div className={styles.info}>INFO</div>
        <div>TARKOV</div>
      </div>


       <TransitionGroup className={styles.navbar}>
        {Object.keys(page_list).map((e) =>
          page_list[e] !== '' && !isMobile ? (
            <CSSTransition
              key={e}
              in={!isMobile}
              timeout={500}
              timein={500}
              classNames={"a_nav_button"}
              unmountOnExit
            >
              <ButtonPage 
                page_index={page_index} 
                page_list={page_list} 
                set_page_index={set_page_index}
                page={e}>
              </ButtonPage>
            </CSSTransition>
          ) : null
        )}
      </TransitionGroup>


      <TransitionGroup className={styles.burger}>
        {isMobile &&
            <CSSTransition
              key={"burger_css_tran"}
              timeout={500}
              timein={500}
              classNames={"a_burger"}
              unmountOnExit

            >
              <Burger page_index={page_index} 
                page_list={page_list} 
                set_page_index={set_page_index}
              ></Burger>
            </CSSTransition>
        }
      </TransitionGroup>

    </div>
  );
}
