import React, { useState, useEffect, useRef } from 'react';
import styles from './target.module.css';

export default function Target({timeOfLife, x, y, color, size, speed}) {

    const [vis, setVis] = useState(true);

    const deadline = Date.now();



    const getTime = () => {

        const time = Date.now() - deadline;

        if(Math.floor(time / 1000) >= timeOfLife * 0.99){
            setVis(false);
        }

    };

    useEffect(() => {
        const interval = setInterval(() => getTime(deadline), 1000);

        return () => clearInterval(interval);
    }, []);


    const style = {backgroundColor : color, left : x, top: y, animationDuration: (timeOfLife) + "s" }
    const [coords, setCoords] = useState({x: 0, y: 0});

    useEffect(() => {
        const handleWindowMouseMove = event => {
        setCoords({
            x: event.clientX,
            y: event.clientY,
        });
        };
        window.addEventListener('mousemove', handleWindowMouseMove);

        return () => {
        window.removeEventListener(
            'mousemove',
            handleWindowMouseMove,
        );
        };
    }, []);



    return (
        <>
        {vis && <div className={styles.target} onClick={(() => checkHit)} style={style}></div>}
        </>
    );
}
