import React, { useEffect, useState } from 'react';
import styles from './Burger.module.css';
import ButtonPage from './ButtonPage';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

export default function Burger({ page_list, set_page_index, page_index, width }) {

    const [is_open_menu, set_open_menu] = useState(false);

    function openBurgeMenu(){

    }

    return (
        <div className={styles.burger}>

            <div className={styles.strip_list} onClick={openBurgeMenu}>
                {[...Array(3)].map((_, i) => (
                    <div className={styles.strip} key={i}></div>
                ))}
            </div>

            {is_open_menu ? <div className={styles.panel_burger} style={{ width: width < 700 ? "100vw" : "40%"}}>
            <TransitionGroup className={styles.panel_burger_button_list}>
                {Object.keys(page_list).map((e) =>
                page_list[e] !== '' ? (
                    <CSSTransition
                    key={e}

                    timeout={500}
                    timein={500}
                    classNames={"a_nav_button"}
                    unmountOnExit
                    >
                    <ButtonPage 
                        page_index={page_index} 
                        page_list={page_list} 
                        set_page_index={set_page_index}
                        page={e}>
                    </ButtonPage>
                    </CSSTransition>
                ) : null
                )}
            </TransitionGroup>

            </div>
            : ""}

        </div>
    );
}
