import React, { useState, useEffect, useLayoutEffect, useRef } from 'react';
import styles from './windowGame.module.css';
import Timer from './timer.jsx';
import Target from './target';



export default function WindowGame({settings, duration, nextWindow}) {


  const colorsOfTargets = ["#EFA94A", "#77DD77", "#FCE883", "#AFDAFC", "3E4717A", "#3EB489", "#CCCCFF", "#FFBD88"] 
  const [statusGame, setStatusGame] = useState(1);
  const [arrayOfTarget, setArrayOfTarget] =  useState([]);


  useEffect(() => {
    const addElement = () => {
      let rect = document.getElementById("screenGame").getBoundingClientRect();

      const size = Math.floor(Math.random() * (settings.size * settings.k) + settings.size);
      setArrayOfTarget((prevElements) => [...prevElements, 
        <Target
          speed={Math.floor(Math.random() * (settings.speed * settings.k) + settings.speed)}
          size={size}
          color={ colorsOfTargets[Math.floor(Math.random()*colorsOfTargets.length)]}
          x={Math.floor(Math.random() * (rect.width - size) + rect.x)}
          y={Math.floor(Math.random() * (rect.height - size) + rect.y)}
          hit={hit}/>]);

    };


    const timeout = setTimeout(() => {
      addElement();
      const interval = setInterval(addElement, 1500);

      return () => clearInterval(interval);
    }, 500);

    return () => clearTimeout(timeout);
  }, []);


  let cntHits = 0;
  let accuracyOfHits = 0;
  let speedOfHits = 0;

  function hit(accuracy, speed){
    cntHits++;
    accuracyOfHits = (accuracyOfHits + accuracy) / 2;
    speedOfHits = (speedOfHits + speed) / 2;
    console.log(cntHits, accuracyOfHits, speedOfHits);
  }

  const [isMobile, setMobile] = useState(window.innerWidth < 600);

  function updateSize(width){
      setMobile(window.innerWidth < 600);
  }

  useEffect(() => {
    window.addEventListener('resize', updateSize);
    return () => {
        window.removeEventListener('resize', updateSize);
    };
  }, []);

  return (

    <div className={'full_size'}>


      <>
        <div className={styles.info_bar}>
          { !isMobile && <div className={styles.left_block}>
            <div>Difficulty level: {settings.name}</div>
          </div>}

          <div className={styles.central_block}>
            <Timer duration={duration} stopedFunc={() => nextWindow()}></Timer>
          </div>

          <div className={styles.right_block}>
            :D
          </div>
        </div>


        <div className={styles.main_div_game} id={"screenGame"}>
        {arrayOfTarget}
        </div>
      </>

    </div>
  );
}
