import React, { useState } from 'react'

import MainTitle from '../../components/MainTitle';
import FinderBlock from './finderBlock.jsx'
import AmmoBlock from './ammoBlock.jsx';

import styles from './mainBlock.module.css';

export default function MainBlock({ammo_list, all_caliber}){

    const [filterAmmo, set_filterAmmo] = useState("All");
    const [isFullTable, setFullTable] = useState(false);
    let divListAmmo = {... ammo_list};

    function updateFullTable(e){
        setFullTable(!isFullTable);
    }

    return (
        <div className={'full_size ' + styles.main_block}>

            <MainTitle title="Таблица патрон" />

            <FinderBlock 
                all_caliber={all_caliber}
                filterAmmo={filterAmmo}
                set_filterAmmo={set_filterAmmo}
                />}

            <AmmoBlock 
                divListAmmo={divListAmmo}
                filterAmmo={filterAmmo}
                setFullTable={setFullTable}/>

        </div>  
    );

}
