import React, { useState, useEffect } from 'react';
import styles from './target.module.css';

export default function Target({timeOfLife, x, y, color, size, speed}) {

    const [vis, setVis] = useState(true);

    const deadline = Date.now();



    const getTime = () => {

        const time = Date.now() - deadline;

        if(Math.floor(time / 1000) >= timeOfLife * 0.99){
            setVis(false);
        }

    };

    useEffect(() => {
        const interval = setInterval(() => getTime(deadline), 1000);

        return () => clearInterval(interval);
    }, []);

    const container = document.querySelector('.container');


    container.addEventListener('click', function(event) {

    const containerRect = container.getBoundingClientRect();
    const containerCenterX = containerRect.x + containerRect.width / 2;
    const containerCenterY = containerRect.y + containerRect.height / 2;

    const clickX = event.clientX;
    const clickY = event.clientY;

    // Вычисляем расстояние от места нажатия до центра контейнера
    const distance = Math.sqrt(
        Math.pow(clickX - containerCenterX, 2) + Math.pow(clickY - containerCenterY, 2)
    );

    // Выводим расстояние в консоль (можно заменить на нужный вам функционал)
    console.log('Расстояние до центра контейнера:', distance);
    });


    const style = {backgroundColor : color, left : x, top: y, animationDuration: (timeOfLife) + "s" }

hit
    return (
        <>
        {vis && <div className={styles.target} onClick={(() => hit())} style={style}></div>}
        </>
    );
}
