import React, { useState } from 'react';
import styles from './windowGame.module.css';
import Timer from './timer.jsx';
import Target from './target';



export default function WindowGame({level, duration}) {



  const [statusGame, setStatusGame] = useState(1);

  function makeArray(size){
    let array = []
    let rectWindow = document.getElementById("screenGame").getBoundingClientRect();
    for(let i = 0; i < size; i++){
      array.push(<Target
       timeOfLife={8}/>)
    }
  }

  return (
    <div className={'full_size'}>

      { statusGame === 1 &&
      <>
        <div className={styles.info_bar}>
          <div className={styles.left_block}>
            <div>Difficulty level: {level}</div>
          </div>

          <div className={styles.central_block}>
            <Timer duration={duration} stopedFunc={() => setStatusGame(2)}></Timer>
          </div>

          <div className={styles.right_block}>
            :D
          </div>
        </div>


        <div className={styles.main_div_game} id="screenGame">
          <Target timeOfLife={44} color={"#fff"} ></Target>
        </div>
      </>}

    </div>
  );
}
