import React, { useState, useEffect } from "react";
import styles from "./ammoBlock.module.css"
import CheckButton from "../../components/CheckButton";
import IconButton from "./iconButton";

export default function AmmoBlock({ divListAmmo, filter_ammo }) {

    const [isMobile, setMobile] = useState(window.innerWidth < 850);
    const [typeOfTable, setTypeOfTable] = useState(0);

    function updateTypeOfTable(idx){
      setTypeOfTable(idx);
    }


    function changeTable(){
      setMobile(window.innerWidth < 850);   
    }

    useEffect(() => {
      const handleResize = () => {
        changeTable();
      };

        window.addEventListener('resize', handleResize);

      return () => {
        window.removeEventListener('resize', handleResize);
      };
    }, []);



    arrayTitle = ["", "", "Урон", "Бронепробитие", "Скорость"]
    console.log("typeOfTable: " + typeOfTable + "\tisMobile: " + isMobile);

    return (
      <div className={styles.div_ammo}>

        <div className={styles.type_of_table}>

          <CheckButton
            isOn={typeOfTable === 0}
            data={0}
            callback={updateTypeOfTable}
            >
          Default
          </CheckButton> 
          <CheckButton
            isOn={typeOfTable === 1}
            data={1}
            callback={updateTypeOfTable}
            >
          Plate
          </CheckButton>



        </div>

        {typeOfTable === 0 ?
        <div className={styles.div_ammo}>
          <div key="titleOfTable" className={styles.table}>
            <div className={styles.row}>
              {arrayTitle.map((e) => {
                <div>{e}</div>
              })}
            </div>
          </div>

          <div key="bodyOfTable" className={styles.table}>

            {Object.keys(divListAmmo).map((caliber, index) => (
              <>
                { (filter_ammo === "All" || caliber === filter_ammo) && 
                  <div key={index} className={styles.mini_table}>
                    <div className={styles.mini_title}>{caliber}</div>
                    {divListAmmo[caliber].map((array, idx) => (
                      <div className={styles.row} key={idx}>
                        {array.map((e, innerIdx) => (
                          <React.Fragment key={innerIdx}>{e}</React.Fragment>
                        ))}
                      </div>
                    ))}
                  </div>
                }
              </>
            ))}

          </div>
        </div> 
        :  
        <div key="bodyOfPlate" className={styles.body_of_plate}>

      </div>}


      </div>
    );

}
