import React, { useState, useEffect } from "react";
import styles from "./ammoBlock.module.css"
import CheckButton from "../../components/CheckButton";
import IconButton from "./iconButton";

export default function AmmoBlock({ div_list_ammo, filter_ammo }) {

    const [isPlate, setPlate] = useState(window.innerWidth);
    const isUserChangePlate = false;


    useEffect(() => {
        const handleResize = () => {
          if(!isUserChangePlate)
            setPlate((window.innerWidth < 850));
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const [sorting_filter, set_sorting_filter] = useState("");

    function updateSortingFilter(value){
      set_sorting_filter(value);
    }

    const [typeOfTable, setTypeOfTable] = useState(0);

    function updateTypeOfTable(idx){
      isUserChangePlate = idx === 1;
      setTypeOfTable(idx);
    }

    console.log("vot isPlate: " + isPlate);

    return (
      <div className={styles.div_ammo}>

        <div className={styles.type_of_table}>


          <IconButton
            isOn={typeOfTable == 0}
            data={0}
            callback={updateTypeOfTable}
            >
              <div className={styles.icon_table}>
                {[...Array(3)].map((_, i) => (
                  <div className={styles.icon_table_lines}></div>
                ))}
              </div>
          </IconButton>

          <IconButton
            data={1}
            callback={updateTypeOfTable}
            >
              <div className={styles.icon_plate}>
                {[...Array(4)].map((_, i) => (
                  <div class={styles.icon_plate_square}></div>
                ))}
              </div>
           </IconButton>


        </div>

        <div className={styles.div_ammo}>
          <div key="titleOfTable" className={styles.table}>
            <div className={styles.row}>
              <div></div>
              <div></div>
              <div>Урон</div>
              <div>Бронепробитие</div>
              <div>Скорость</div>
            </div>
          </div>

          <div key="bodyOfTable" className={styles.table}>

            {Object.keys(div_list_ammo).map((caliber, index) => (
              <>
                { (filter_ammo === "All" || caliber === filter_ammo) && 
                  <div key={index} className={styles.mini_table}>
                    <div className={styles.mini_title}>{caliber}</div>
                    {div_list_ammo[caliber].map((array, idx) => (
                      <div className={styles.row} key={idx}>
                        {array.map((e, innerIdx) => (
                          <React.Fragment key={innerIdx}>{e}</React.Fragment>
                        ))}
                      </div>
                    ))}
                  </div>
                }
              </>
            ))}

          </div>
        </div>


      </div>
    );

}
