import React, { useEffect, useState } from 'react';
import styles from './Burger.module.css';
import ButtonPage from './ButtonPage';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

export default function Burger({ page_list, set_page_index, page_index, width }) {

    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    useEffect(() => {
        const handleResize = () => {
        setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
    }, []);

  const isMobile = windowWidth < 850;

    const [is_open_menu, set_open_menu] = useState(false);

    function openBurgeMenu(){
        set_open_menu(!is_open_menu);
    }

    function clickPage(idx){
        openBurgeMenu();
        set_page_index(idx);
    }

    return (
        <div className={styles.burger}>

            <div className={styles.strip_list} onClick={openBurgeMenu}>
                {[...Array(3)].map((_, i) => (
                    <div className={styles.strip} key={i}></div>
                ))}
            </div>

            <TransitionGroup>
            {is_open_menu  && 
                <CSSTransition
                key={"burger_panel_trans"}
                  timeout={500}
                  timein={500}
                  classNames={"a_panel_burger"}
                  unmountOnExit

                  >
                    <div className={styles.panel_burger} style={{ width: width < 700 ? "100vw" : "40%"}}>
                            <div className={styles.close_panel_burger}>1</div>
                        <TransitionGroup className={styles.panel_burger_button_list}>
                            {Object.keys(page_list).map((e) =>
                            page_list[e] !== '' ? (
                                <ButtonPage 
                                    page_index={page_index} 
                                    page_list={page_list} 
                                    set_page_index={clickPage}
                                    page={e}>
                                </ButtonPage>
                            ) : null
                            )}
                        </TransitionGroup>
                    </div>
                </CSSTransition>
            }
            </TransitionGroup>

        </div>
    );
}
